mapper.select:
SELECT c.post_id, c.title, c.content, c.view_count, c.like_count, c.comment_count, c.created_at, c.updated_at, c.user_id, m.nickname, c.dong_code, d.dong_name, c.category_id, cc.category_name, (SELECT ci.image_url FROM COMMUNITY_IMAGE ci WHERE ci.post_id = c.post_id AND ci.deleted_at IS NULL ORDER BY ci.sort_order LIMIT 1) AS thumbnail_url FROM COMMUNITY c LEFT JOIN COMMUNITY_CATEGORY cc ON c.category_id = cc.category_id LEFT JOIN MEMBERS m ON c.user_id = m.user_id LEFT JOIN LEGAL_DONG d ON c.dong_code = d.dong_code
c.deleted_at IS NULL
AND c.dong_code = #{dongCode}
AND c.category_id = #{categoryId}
AND (c.title LIKE CONCAT('%', #{searchKeyword}, '%') OR c.content LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY c.created_at DESC LIMIT #{startIdx}, 8

SELECT COUNT(*) FROM COMMUNITY c
c.deleted_at IS NULL
AND c.dong_code = #{dongCode}
AND c.created_at >= DATE_SUB(NOW(), INTERVAL 7 DAY)
AND c.category_id = #{categoryId}
AND (c.title LIKE CONCAT('%', #{searchKeyword}, '%') OR c.content LIKE CONCAT('%', #{searchKeyword}, '%'))

mapper.update:
UPDATE COMMUNITY_COMMENT
SET group_id = #{groupId}
WHERE comment_id = #{commentId}

mapper.insert:
INSERT INTO COMMUNITY (
    title
    , content
    , dong_code
    , category_id
    , user_id
) VALUES (
    #{title}
    , #{content}
    , #{dongCode}
    , #{categoryId}
    , #{userId}
)

mapper.update:
UPDATE COMMUNITY
SET title = #{title}
, content = #{content}
, dong_code = #{dongCode}
, category_id = #{categoryId}
, updated_at = NOW()
WHERE post_id = #{postId} AND user_id = #{userId}

UPDATE COMMUNITY
SET deleted_at = NOW()
WHERE post_id = #{postId} AND user_id = #{userId}

mapper.select:
SELECT c.post_id, c.title, c.content, c.view_count, c.like_count, c.comment_count, c.created_at, m.nickname, c.dong_code, d.dong_name, c.category_id, cc.category_name, (c.like_count * 3 + c.view_count * 1 + c.comment_count * 2) AS popularity_score FROM COMMUNITY c LEFT JOIN COMMUNITY_CATEGORY cc ON c.category_id = cc.category_id LEFT JOIN MEMBERS m ON c.user_id = m.user_id LEFT JOIN LEGAL_DONG d ON c.dong_code = d.dong_code
c.deleted_at IS NULL AND c.created_at >= DATE_SUB(NOW(), INTERVAL 7 DAY) AND c.post_id != #{currentPostId}
AND c.dong_code = #{dongCode}
ORDER BY popularity_score DESC, c.created_at DESC LIMIT 8

mapper.insert:
INSERT INTO COMMUNITY_IMAGE (
