mapper.select:
SELECT c.post_id, c.title, c.content, c.view_count, c.like_count, c.created_at, c.updated_at, c.user_id, m.nickname, c.dong_code, d.dong_name, c.category_id, cc.category_name FROM COMMUNITY c LEFT JOIN COMMUNITY_CATEGORY cc ON c.category_id = cc.category_id LEFT JOIN MEMBERS m ON c.user_id = m.user_id LEFT JOIN LEGAL_DONG d ON c.dong_code = d.dong_code
c.deleted_at IS NULL
AND c.dong_code = #{dongCode}
AND c.category_id = #{categoryId}
AND (c.title LIKE CONCAT('%', #{searchKeyword}, '%') OR c.content LIKE CONCAT('%', #{searchKeyword}, '%'))
ORDER BY c.created_at DESC
